using System;
using Agrirouter.Api.Dto.Onboard;
using Xunit;

namespace Agrirouter.Test.Service.Common;

/// <summary>
/// Parse a router device.
/// </summary>
public class ParseRouterDeviceTest
{
    private const string Json =
        "{\"authentication\":{\"type\":\"P12\",\"secret\":\"!oBTOQzcOXEYS?qZA5ioBu98QFMzvLQT!Zjr\",\"certificate\":\"\"},\"deviceAlternateId\":\"dd5abe2d-06aa-40a3-9368-ab094305c539\",\"connectionCriteria\":{\"clientId\":\"7ea2a9f9-d6fd-40cd-8515-886a2c7864c8\",\"gatewayId\":\"2\",\"host\":\"dke-qa.eu10.cp.iot.sap\",\"port\":8883}}";

    [Fact]
    public void GivenValidJsonWhenParsingTheRouterDeviceTheOutcomeShouldBeValidAsWell()
    {
        var routerDevice = RouterDevice.FromJson(Json);
        Assert.NotNull(routerDevice);
        Assert.Equal("dd5abe2d-06aa-40a3-9368-ab094305c539", routerDevice.DeviceAlternateId);

        Assert.Equal("P12", routerDevice.Authentication.Type);
        Assert.Equal("!oBTOQzcOXEYS?qZA5ioBu98QFMzvLQT!Zjr", routerDevice.Authentication.Secret);
        Assert.Equal(
            "",
            routerDevice.Authentication.Certificate);

        Assert.Equal("dke-qa.eu10.cp.iot.sap", routerDevice.ConnectionCriteria.Host);
        Assert.Equal("7ea2a9f9-d6fd-40cd-8515-886a2c7864c8", routerDevice.ConnectionCriteria.ClientId);
        Assert.Equal("2", routerDevice.ConnectionCriteria.GatewayId);
        Assert.Equal(8883, routerDevice.ConnectionCriteria.Port);
    }

    [Fact]
    public void GivenInvalidJsonWhenParsingTheRouterDeviceThereShouldBeAnException()
    {
        Assert.Throws<ArgumentException>(() => RouterDevice.FromJson("invalid json"));
    }
}