    public class X509CertificateServiceTest : AbstractIntegrationTestForCommunicationUnits
    {
        [Fact]
        public void
            GivenValidOnboardingResponseWithP12CertificateWhenCreatingCertificateThenTheCertificateCreationShouldNotThrowAnyError()
        {
            var certificate = X509CertificateService.GetCertificate(OnboardResponseWithP12Certificate);
            Assert.NotNull(certificate);
        }

        [Fact]
        public void
            GivenValidOnboardingResponseWithPemCertificateWhenCreatingCertificateThenTheCertificateCreationShouldNotThrowAnyError()
        {
            var certificate = X509CertificateService.GetCertificate(OnboardResponseWithPemCertificate);
            Assert.NotNull(certificate);
        }

        [Fact]
        public void
            GivenValidRouterDeviceWithP12CertificateWhenCreatingCertificateThenTheCertificateCreationShouldNotThrowAnyError()
        {
            var certificate = X509CertificateService.GetCertificate(RouterDeviceWithP12Certificate);
            Assert.NotNull(certificate);
        }

        [Fact]
        public void
            GivenValidRouterDeviceWithPemCertificateWhenCreatingCertificateThenTheCertificateCreationShouldNotThrowAnyError()
        {
            var certificate = X509CertificateService.GetCertificate(RouterDeviceWithPemCertificate);
            Assert.NotNull(certificate);
        }


